CREATE MATERIALIZED VIEW "public"."readings_5min_agg" WITH (timescaledb.continuous)  AS (select time_bucket('5 minutes', "time") as "bucket", "name", "unit", "category", "system_id", avg("value") as "avg", min("value") as "min", max("value") as "max", count(*) as "count", first("time", "time") as "first", last("time", "time") as "last" from "readings" group by "readings"."system_id", "readings"."category", "readings"."unit", "readings"."name", "bucket") WITH NO DATA;